create table users
(
    ID            int auto_increment
        primary key,
    USER_NAME     varchar(50)                           not null,
    EMAIL         varchar(100)                          not null,
    PASSWORD      varchar(255)                          not null,
    AVATAR        varchar(255)                          null,
    LAST_LOGIN_DT datetime                              null,
    USED_YN       varchar(1)  default 'Y'               null,
    REG_DT        datetime    default CURRENT_TIMESTAMP null,
    REG_ID        int                                   null,
    MOD_DT        datetime                              null,
    MOD_ID        int                                   null,
    SSO_ID        varchar(50)                           null,
    LOGIN_TYPE    varchar(20) default 'SELF'            null,
    EMAIL_SEND_YN varchar(1)                            null,
    EMAIL_SEND_DT datetime                              null,
    VERIFY_YN     varchar(1)                            null,
    VERIFY_DT     datetime                              null,
    VERIFY_CODE   varchar(6)                            null,
    constraint EMAIL
        unique (EMAIL)
);

